mapper.select:
SELECT IFNULL(max(boardNo)+1,1) AS boardNo 
FROM Board
WHERE categoryCode = #{categoryCode}

SELECT boardNo, categoryCode, userNo, title, contents, createDT, modifyDT
FROM Board
WHERE deleteDT IS NULL
AND boardNo = #{boardNo}
AND categoryCode = #{categoryCode}

mapper.update:
UPDATE Board SET title = #{title}, contents = #{contents}, modifyDT = NOW()
WHERE boardNo = #{boardNo}
AND categoryCode = #{categoryCode}
